#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;
vector <long long> res;
int n, cnt;
matrix a, check;

int main() {
    faster();
    long long a[5000];
    int n;
    cin >> n;
    a[1] = 1;
    long long tmp = 1;
    for (int i = 2 ; i <= n ; i++)
    {
        tmp *= i;
        a[i] = a[i-1] + tmp;
    }
    cout << a[n];
}

