#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;
vector <long long> res;
int n, cnt;
matrix a, check;

int main() {
    faster();
    int t;
    cin >> t;
    while ( t-- )
    {
        int check = 0;
        string s;
        cin >> s;
        for (int i = 0 ; i < s.size() - 1 ; i++)
        {
            if ( abs(s[i]-s[i+1]) != 1 )
            {
                check++;
                break;
            }
        }
        if ( check == 0 ) cout << "YES";
        else cout << "NO";
        if ( t != 0 )
        {
            cout << endl;
        }
    }
}

