#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;
vector <long long> res;
int n, cnt;
matrix a, check;

int main() {
    faster();
    int t;
    cin >> t;
    while ( t-- )
    {
        double x1, y1, x2, y2;
        cin >> x1 >> y1 >> x2 >> y2;
        cout << setprecision(4) << fixed << sqrt(pow(x2-x1,2)+pow(y2-y1,2));
        if ( t != 0 )
        {
            cout << endl;
        }
    }
}

