#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
#define el "\n"
const long long big = 1e6;

vector <bool> Primes(1e6+5,true);
vector <long long> PrimeNumbs;

void SieveOfEratosthenes() {
    Primes[0] = Primes[1] = false;
    for (int i = 2 ; i * i <= 1e6 ; i++)
    {
        if ( Primes[i] == true )
        {
            for (int j = i * i ; j <= 1e6 ; j += i)
            {
                Primes[j] = false;
            }
        }
    }
	for (int i = 2 ; i <= 1e6 ; i++)
	{
		if ( Primes[i] == true )
		{
			PrimeNumbs.push_back(i);
		}
	}
}

int main() {
	faster();
	int t;
	cin >> t;
	SieveOfEratosthenes();
	while ( t-- )
	{
		int n, cnt = 0;
		cin >> n;
		int i = 0;
		while ( i <= n )
		{
			int cnt1 = 0;
			while ( n % PrimeNumbs[i] == 0 )
			{
				n /= PrimeNumbs[i];
				cnt++;
				cnt1++;
			}
			if ( cnt1 > 1 ) cnt = 100;
			i++;
		}
		if ( cnt == 3 ) cout << 1;
		else cout << 0;
		if ( t != 0 )
		{
			cout << endl;
		}
	}
}

