#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;

long long a[10000];

void sang() {
    a[1] = 1;
    for (long long i = 2 ; i < 1000 ; i++)
    {
       int tmp = __gcd(a[i-1],i);
       a[i] = a[i-1] * i / tmp;
    }
}

int main() {
    faster();
    sang();
    int t;
    cin >> t;
    while ( t-- )
    {
        int n;
        cin >> n;
        cout << a[n];
        if ( t != 0 )
        {
            cout << endl;
        }
    }
}

