#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;

bool isPrime[100001];

void Erastothenes() {
    for (int i = 0 ; i <= 100000 ; i++)
	{
        isPrime[i] = true;
    }
    isPrime[0] = false;
    isPrime[1] = false;
    for (int i = 2 ; i * i <= 100000 ; i++)
	{
        if ( isPrime[i] == true )
		{
            for (int j = i * i ; j <= 100000 ; j += i)
            {
                isPrime[j] = false;
            }
        }
    }
}

int main() {
    faster();
    // Erastothenes();
    int check = 0;
    int n;
    cin >> n;
    if ( n == 0 || n == 1 )
    {
        cout << "NO";
        return 0;
    }
    for (int i = 2 ; i <= sqrt(n) ; i++)
    {
        if ( n % i == 0 )
        {
            check = 1;
            break;
        }
    }
    if ( check == 0 ) cout << "YES";
    else cout << "NO";
}

