#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;
vector <long long> res;

int main() {
    faster();
    int t;
    cin >> t;
    while ( t-- )
    {
        int n, check = 0;
        cin >> n;
        vector <long long> a;
        int x;
        for (int i = 0 ; i < n ; i++)
        {
            cin >> x;
            if ( x > 0 ) a.push_back(x);
        }
        sort(a.begin(),a.end());
        if ( a[0] != 1 ) cout << 1;
        else
        {
            for (int i = 0 ; i < n - 1 ; i++)
            {
                if ( abs(a[i]-a[i+1]) > 1 )
                {
                    cout << a[i] + 1;
                    check = 1;
                    break;
                }
            }
            if ( check == 0 ) cout << a[n-1] + 1;
        }
        if ( t != 0 )
        {
            cout << endl;
        }
    }
}

