#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;
vector <long long> res;

int main() {
    faster();
    int t;
    cin >> t;
    while ( t-- )
    {
        int n, k;
        cin >> n >> k;
        vector <int> a(n);
        for (int i = 0 ; i < n ; i++)
        {
            cin >> a[i];
        }
        for (int i = 0 ; i < k ; i++)
        {
            int l, r, sum = 0;
            cin >> l >> r;
            for (int i = l-1 ; i < r ; i++)
            {
                sum += a[i];
            }
            cout << sum;
            if ( i != k - 1 ) cout << endl;
        }
        if ( t != 0 )
        {
            cout << endl;
        }
    }
}

