#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;
vector <long long> res;

int main() {
    faster();
    int t = 1;
    while ( t-- )
    {
        int n, cnt, res = 0;
        cin >> n;
        matrix a;
        a.assign(n, vector <long long> (3,0));
        for (int i = 0 ; i < n ; i++)
        {
            cnt = 0;
            for (int j = 0 ; j < 3 ; j++)
            {
                cin >> a[i][j];
                if ( a[i][j] == 1 ) cnt++;
            }
            if ( cnt >= 2 ) res++;
        }
        cout << res;
        // if ( t != 0 )
        // {
        //     cout << endl;
        // }
    }
}

