#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;
vector <long long> res;

int main() {
    faster();
    int t;
    cin >> t;
    while ( t-- )
    {
        vector <int> b(1000005,0);
        int n;
        cin >> n;
        vector <int> a(n);
        int MAX = 0, res = 0;
        for (int i = 0 ; i < n ; i++)
        {
            cin >> a[i];
            b[a[i]]++;
            MAX = max(MAX,a[i]);
        }
        for (int i = 0 ; i <= MAX ; i++)
        {
            if ( b[i] > 1 ) res += b[i];
        }
        cout << res;
        if ( t != 0 )
        {
            cout << "\n";
        }
    }
}

