#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
const long long big = 1e6;

int main() {
    faster();
    int t;
    cin >> t;
    cin.ignore();
    while ( t-- )
    {
        string s;
        getline(cin,s);
        int res = 0;
        for (int i = 0 ; i < s.size() ; i++)
        {
            if ( s[i] == ' ' || s[i] == '\t' || s[i] == '\n' )
            {
                res++;
            }
        }
        cout << res + 1;
        if ( t != 0 )
        {
            cout << "\n";
        }
    }
}

