#include <bits/stdc++.h>
using namespace std;
#define faster() ios_base::sync_with_stdio(false);cin.tie();cout.tie();
#define matrix vector <vector<long long>>
#define MOD 1000000007
#define el "\n"
const long long big = 1e6;

int main() {
	int t;
	cin >> t;
	while ( t-- )
	{
		int n;
		cin >> n;
		int a[n];
		int min = 1;
		for (int i = 0 ; i < n-1 ; i++)
		{
			cin >> a[i];
			if ( a[i] < 0 ) a[i] = 0;
			if ( a[i] == min ) min++;
		}
		cout << min; 
		if ( t != 0 )
		{
			cout << endl;
		}
	}
}

